import React, { useState } from 'react';
import { StyleSheet, Text, View, Image, TextInput, Pressable } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';
import HeaderScreen from './HeaderScreen';
import OptionScreen from './OptionScreen';

const LogScreen = ({ navigation }) => {

    const [showOptions, setShowOptions] = useState(true);

    return (
        <SafeAreaView style={styles.global}>

            {/* Header */}

            <View style={styles.header}>
                <Pressable onPress={setShowOptions(true)}>
                    <Image source={require('./../assets/svg/options.png')} style={styles.png}/>
                </Pressable>
                
                <Text style={styles.title}>{title}</Text>

                <Pressable>
                    <Image source={require('./../assets/svg/mag-glass.png')} style={styles.png}/>
                </Pressable>

                <Pressable>
                    <Image source={require('./../assets/svg/bell.png')} style={styles.png}/>
                </Pressable>
            </View>
            <HeaderScreen 
                title={"nik"} 
                showOptions={showOptions} 
                setShowOptions={setShowOptions}/>
            <View style={styles.mainBody}>
                <Text>Coucou</Text>
            </View>

            { showOptions === true ? <OptionScreen 
                                        showOptions={showOptions} 
                                        setShowOptions={setShowOptions}/> : null }
            
        </SafeAreaView>
    )
}

export default LogScreen;

const styles = StyleSheet.create({
    global: {
        backgroundColor: "rgb(235, 235, 235)",
        flex: 1
    },

    mainBody: {
        flex: 1,
        backgroundColor: "grey"
    }
})
