import React, { useState } from 'react';
import { StyleSheet, Text, View, Image, TextInput, Pressable } from 'react-native';
import { SafeAreaView } from 'react-native-safe-area-context';

const blue = "rgb(70, 130, 180)";
const iconsSize = 25;

const LogScreen = ({ navigation }) => {

    const [showOptions, setShowOptions] = useState(true);

    return (
        <SafeAreaView style={styles.global}>

            {/* Header */}

            <View style={styles.header}>
                <Pressable onPress={setShowOptions(true)}>
                    <Image source={require('./../assets/svg/options.png')} style={styles.png}/>
                </Pressable>
                
                <Text style={styles.title}>Titre</Text>

                <Pressable>
                    <Image source={require('./../assets/svg/mag-glass.png')} style={styles.png}/>
                </Pressable>

                <Pressable>
                    <Image source={require('./../assets/svg/bell.png')} style={styles.png}/>
                </Pressable>
            </View>


            <View style={styles.mainBody}>
                <Text>Coucou</Text>
            </View>

            { showOptions === true ?
            
            <View style={styles.globalOptions}>
                <View style={styles.left}>
                    <Text>Left</Text>
                </View>
            
                <Pressable onPress={setShowOptions(false)}>
                    <View  style={styles.right}>
                        <Text>Right</Text>
                    </View>
                </Pressable>
            
            </View>
            
            : null }
            
        </SafeAreaView>
    )
}

export default LogScreen;

const styles = StyleSheet.create({
    global: {
        backgroundColor: "rgb(235, 235, 235)",
        flex: 1
    },

    mainBody: {
        flex: 1,
        backgroundColor: "grey"
    },

    header: {
        flexDirection: 'row',
        backgroundColor: blue,
        height: 60,
        alignItems: 'center'
    },

    png: {
        height: iconsSize,
        width: iconsSize,
        tintColor: "white",
        marginLeft: 10,
        marginRight: 10
    },

    title: {
        color: "white",
        flex: 1,
        fontSize: 25,
        marginLeft: 20
    },

    globalOptions: {
        flexDirection: "row",
        position: "absolute",
        width: "100%",
        height: "100%"
    },

    left: {
        flex: 4,
        backgroundColor: "red",
    },

    right: {
        flex: 1,
        backgroundColor: "blue",
    }
})
